def price_pred(tot_sqft, bed, bath, loc):
    loc_index = l.index(loc)
    x = np.zeros(len(l))
    x[0] = tot_sqft
    x[1] = bath
    x[2] = bed
    if loc_index>=0:
        x[loc_index] = 1
    
    print(loc_index)
    print(x)
    prediction = model.predict([x])[0]
    print(prediction)
    return prediction

def main():
    st.title("Bangalore House Price Prediction")
    html_temp = """
    <div style="background-color:rgb(68, 65, 65);padding:10px">
    <h2 style="color:rgb(241, 153, 20);text-align:center;">WebApp for House Price Prediction</h2>
    </div>
    """
    st.markdown(html_temp,unsafe_allow_html=True)

    tot_sqft = st.text_input('Total SQFT  :',1000)
    tot_sqft = int(tot_sqft)

    bed = st.slider('Enter number of bedrooms :',min_value=1, max_value=5, value=1, step=1)
    bed = int(bed)

    bath = st.slider('Enter number of bathrooms :',min_value=1, max_value=5, value=1, step=1)
    bath = int(bath)
    
    loc = st.selectbox('Select location : ', l[3::])
    result=""
    if st.button("Estimate Price"):
        result=price_pred(tot_sqft, bed, bath, loc)
        #print(result)
        result = round(result,2)
        st.success('The Estimated price of the house is {} Lakhs'.format(result))
    
    
    if st.button("About"):
        st.text("This is House Price Prediction Model for Bangalore City made using StreamLit")
        st.text("Model by JAISRI V")

if __name__=='__main__':
    main()
